//异步
function copyfile(file1,file2) {
    var fs = require("fs");
    fs.readFile(file1,"",(err1,data1)=>{
        if(err1){
            console.error(err1)
        }else {
            fs.writeFile(file2,data1,{
                flag:"a"
            },(err2)=>{
                if (err2){
                    console.error(err2)
                }else {
                    console.log("复制成功")
                }
            })

        }
    })

};
//同步
function copyfileSync(file1,file2) {
    var fs = require("fs");
    var data = fs.readFileSync(file1);
    fs.writeFileSync(file2,data,{flag:"a"});
};
module.exports = {
    copyfile:copyfile,
    copyfileSync:copyfileSync,

}